CREATE TABLE IF NOT EXISTS Page (id INTEGER PRIMARY KEY, titolo VARCHAR(30) NOT NULL, creationDate DATE NOT NULL, lastModifiedDate DATE NOT NULL, "index" INTEGER NOT NULL);
CREATE TABLE IF NOT EXISTS Note (id INTEGER PRIMARY KEY, text TEXT NOT NULL, creationDate DATE NOT NULL, lastModifiedDate DATE NOT NULL, positionX INTEGER NOT NULL, positionY INTEGER NOT NULL, sizeX INTEGER NOT NULL, sizeY INTEGER NOT NULL);
CREATE TABLE IF NOT EXISTS PageNote (idPage INTEGER NOT NULL, idNote INTEGER NOT NULL, PRIMARY KEY (idPage, idNote), FOREIGN KEY (idPage) REFERENCES Page(id) ON DELETE CASCADE, FOREIGN KEY (idNote) REFERENCES Note(id) ON DELETE CASCADE);
